import { useEffect, useState  } from 'react';

export const useCategorias=()=>{

   const [dataItems, setdataItems] = useState([]);

   const [loteCategorias, setLoteCategorias] = useState([]);

    //CUANDO EL COMPONENTE SE MONTE
    useEffect(() => {

            fetch(`http://localhost:8080/api/categorias?desde=0&hasta=${Infinity}`,{
            method: 'GET', 
            headers: {
              'Content-Type': 'application/json',
              'x-token':"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1aWQiOiI2MWIzZTg4ZGU0NDdlOTRjMzYxNmU1NzkiLCJpYXQiOjE2NTIxMTE5NDgsImV4cCI6MTY1MjEyNjM0OH0.YtWF6QdsUUjfmdVf6xf4ob4XbN3DAm4vzg955REHmUM"
            }
            })

            .then(dataFect=>dataFect.json())

            .then((data)=>setdataItems(data.categorias))

            .catch(error=>console.log(error));

    },[]);

    //CUANDO EL ESTADO dataItems SE ACTUALICE
    useEffect(() => {


      setLoteCategorias(dataItems.slice(0,5));


     },[dataItems]);


     const nextCategorias=(idPaginate) => {
        console.log(idPaginate);
        const data=dataItems.slice(5*idPaginate-5,Infinity);

        setLoteCategorias(data.slice(0,5));

    }



    return {total:dataItems.length,loteCategorias,nextCategorias,totalPage:Math.ceil(dataItems.length/5)};
}
