//IMPORTACIONES
import { Button } from 'react-bootstrap';
import {React,useEffect,useState} from 'react';
import '../App.css';


export default function ItemCategoria({title,nameCategoria,cantidadProducto,update}) {

  const [first, setfirst] = useState(0);


  useEffect(() => {
    setfirst(update);
  }, [update])



  return (
   <Button  variant="outline-primary " title={title} className='animacionBarra m-2 rounded-pill position-relative botontamano'>
     <span className='textoBotn'>{nameCategoria}</span>
     <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">{cantidadProducto}</span>
   </Button>
  )
}
