//IMPORTACIONES
import { Button, Col } from 'react-bootstrap';
import {React,useState,useEffect} from 'react';
import ItemCategoria from './ItemCategoria';
import '../App.css';
import { useCategorias } from '../Hooks/useCategorias.js';

export default function BarraCategorias() {

 const {total : totalCategorias,loteCategorias,nextCategorias}= useCategorias();






  return(

    <Col md lg="7" className='tamanoPanelData nav center-items-nav justify-content-center pt-2 pb-2 rounded segundoColor me-5 mb-5'>

        {totalCategorias==0  ?  "" : <Button onClick={()=>nextCategorias()} className=' rounded-pill '>Back</Button>}

        {totalCategorias==0 ? <h3>No hay Categorias</h3>  :  loteCategorias.map(({nombre,cantidadProducto})=> <ItemCategoria  title={nombre} nameCategoria={nombre} cantidadProducto={cantidadProducto}/>) }

        {totalCategorias>5  ?  <Button className='rounded-pill' onClick={()=>nextCategorias()}>Next</Button> : ""}

    </Col>

  )

}
