import {React,createContext,useState} from 'react';

//CREANDO EL CONTEXTO
export const productListContext=createContext('');

export function NombreProducto({children}) {
  
    const [productName, setNameProduct] = useState('');

   const [updateProductList,setUpdateProductList]  = useState(false);

    const contextValue={

        productName,
        setNameProduct,
        updateProductList,
        setUpdateProductList
    };

    return (
   <productListContext.Provider value={contextValue}>
       {children}
    </productListContext.Provider >
  )
}
