export const category=createContext("");

export  function CategorySelected({children}) {

    const [categorySeleccionada, setCategorySeleccionada] = useState('');

    const [categorias, setCategorias] = useState([]);

    const getCategorias=() => {

      fetch(`http://localhost:8080/api/categorias?desde=0&hasta=${Infinity}`,{
        method: 'GET', 
        headers: {
          'Content-Type': 'application/json',
          'x-token':"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1aWQiOiI2MWIzZTg4ZGU0NDdlOTRjMzYxNmU1NzkiLCJpYXQiOjE2NTIzNjU2ODEsImV4cCI6MTY1MjM4MDA4MX0.nZ6zIZz_XOEkYv4dseKOjQitf5G0fV8HVRu6sJ0cLow"
        }
        })

        .then(dataFect=>dataFect.json())

        .then(({categorias})=>setCategorias(categorias))

        .catch(error=>console.log(error));

     }


    const contextValue={
      categorySeleccionada,
      categorias,
      setCategorias,
      getCategorias
    };

return (
    <category.Provider value={contextValue}>

        {children}

    </category.Provider>
  )
}
