import { Button, Col } from 'react-bootstrap';
import {React,useState,useEffect} from 'react';
import ItemCategoria from './ItemCategoria';
import '../App.css';
import { useCategorias } from '../Hooks/useCategorias.js';

export default function BarraCategorias() {

 const {dataItems,totalCategorias}= useCategorias();

  const [loteCategorias, setLoteCategorias] = useState(dataItems.slice(0,5));


  const nextCategorias=() => { 


      console.log('Primer lote=',loteCategorias);

   }


  return(

    <Col md lg="7" className='tamanoPanelData nav center-items-nav justify-content-center pt-2 pb-2 rounded segundoColor me-5 mb-5'>

        {dataItems.length==0  ?  "" : <Button  className=' rounded-pill '>Back</Button>}

        {dataItems.length==0 ? <h3>No hay Categorias</h3>  :  dataItems.map(({nombre,cantidadProducto})=> <ItemCategoria  title={nombre} nameCategoria={nombre} cantidadProducto={cantidadProducto}/>) }

        {totalCategorias>5  ?  "" : <Button className='rounded-pill' onClick={()=>nextCategorias()}>Next</Button>}

    </Col>

  )

}
